import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import "bootstrap/dist/css/bootstrap.min.css";
import './style/attractions.css'

import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
// Single Exercise Component
const Attraction = (props) => {
  return (
    <div className="">
    <Card className="attractionsCard" style={{ width: '60vw' }}>
      <Card.Img variant="top" src={props.attraction.imageURL} />
      <Card.Body>
        <Card.Title>{props.attraction.name}</Card.Title>
        <Card.Text>
          <a href= {props.attraction.website}>Website</a>
        </Card.Text>
        <Button variant="light"><Link to={"/attractions/" + props.attraction._id}>learn more</Link></Button>
      </Card.Body>
    </Card>
    </div>

    // <tr>
    //   <td>{props.attraction.name}</td>
    //   <td><img className="attractionsIMG"src={props.attraction.imageURL}></img></td>
    //   <td><a href= {props.attraction.website}>Website</a></td>
    //   <td>
    //     <Link to={"/attractions/" + props.attraction._id}>learn more</Link>|{" "}
    //   </td>
    // </tr>
  )
}
// ExerciseList Component
export default class ExercisesList extends Component {
  constructor(props){
    super(props)
    this.state={
      attractions: []
    };
  }
  componentDidMount(){
    axios.get('http://localhost:5000/attractions/')
      .then(response => {
        this.setState({
          attractions: response.data
        })
        console.log('this is the class project')
      })
      .catch((error) => {
        console.log(error)
      })
  }
  exerciseList(){
    return this.state.attractions.map((currentAttraction) =>{
      return <Attraction
        attraction = {currentAttraction}
        key={currentAttraction._id}
      />
    })
  }
  render() {
    return (
      <div>
        <h3>Attractions</h3>
        <div className="attractionsInnerContainer">
        {/* <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Website</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {this.exerciseList()}
          </tbody>
        </table> */}

            {this.exerciseList()}
        </div>
      </div>
    )
  }
}
